import React, { useState } from 'react';
import { createUser } from '../services/api';
import { TextField, Button, Typography, Box, Select, MenuItem, InputLabel, FormControl } from '@mui/material';

const UserForm = ({ onUserCreated }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('regular_user');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createUser({ username, password, role });
      setUsername('');
      setPassword('');
      setRole('regular_user');
      onUserCreated();
    } catch (error) {
      console.error('Error creating user', error);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
      <Typography variant="h5">Create User</Typography>
      <TextField
        label="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        fullWidth
        margin="normal"
      />
      <TextField
        label="Password"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        fullWidth
        margin="normal"
      />
      <FormControl fullWidth margin="normal">
        <InputLabel>Role</InputLabel>
        <Select value={role} onChange={(e) => setRole(e.target.value)}>
          <MenuItem value="regular_user">Regular User</MenuItem>
          <MenuItem value="admin">Admin</MenuItem>
          <MenuItem value="project_manager">Project Manager</MenuItem>
        </Select>
      </FormControl>
      <Button type="submit" variant="contained" color="primary">
        Create
      </Button>
    </Box>
  );
};

export default UserForm;
