import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const api = {
  // Existing endpoints for user and project management...

  // Task management
  createTask: (projectId, taskData, token) => axios.post(`${API_URL}/projects/${projectId}/tasks`, taskData, { headers: { Authorization: `Bearer ${token}` } }),
  getTasks: (projectId, token) => axios.get(`${API_URL}/projects/${projectId}/tasks`, { headers: { Authorization: `Bearer ${token}` } }),
  updateTask: (projectId, taskId, taskData, token) => axios.put(`${API_URL}/projects/${projectId}/tasks/${taskId}`, taskData, { headers: { Authorization: `Bearer ${token}` } }),
  deleteTask: (projectId, taskId, token) => axios.delete(`${API_URL}/projects/${projectId}/tasks/${taskId}`, { headers: { Authorization: `Bearer ${token}` } }),

  // File sharing
  uploadFile: (projectId, file, token) => {
    const formData = new FormData();
    formData.append('file', file);
    return axios.post(`${API_URL}/files/${projectId}/upload`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${token}`,
      },
    });
  },
  downloadFile: (fileId, token) => axios.get(`${API_URL}/files/${fileId}/download`, { headers: { Authorization: `Bearer ${token}` } }),
  deleteFile: (fileId, token) => axios.delete(`${API_URL}/files/${fileId}`, { headers: { Authorization: `Bearer ${token}` } }),
};

export default api;
