import React, { useState, useEffect } from 'react';
import { createTask, getTasks, updateTask, deleteTask } from '../../services/api';
import { Container, Typography, Button, TextField, List, ListItem, Box, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const TaskManagement = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ projectId: '', title: '', description: '', status: '', dueDate: '', assignedTo: '' });
  const [editTask, setEditTask] = useState(null);

  const loadTasks = async (projectId) => {
    try {
      const response = await getTasks(projectId);
      setTasks(response.data);
      console.log('Tasks loaded:', response.data);
    } catch (error) {
      console.error('Error loading tasks', error);
    }
  };

  const handleCreateTask = async () => {
    console.log('Creating task with data:', newTask);
    try {
      await createTask(newTask.projectId, newTask.title, newTask.description, newTask.status, newTask.dueDate, newTask.assignedTo);
      console.log('Task created successfully');
      loadTasks(newTask.projectId);
      setNewTask({ projectId: '', title: '', description: '', status: '', dueDate: '', assignedTo: '' });
    } catch (error) {
      console.error('Error creating task', error);
    }
  };

  const handleUpdateTask = async () => {
    console.log('Updating task with data:', editTask);
    try {
      await updateTask(editTask.id, editTask.projectId, { title: editTask.title, description: editTask.description, status: editTask.status, dueDate: editTask.dueDate, assignedTo: editTask.assignedTo });
      console.log('Task updated successfully');
      loadTasks(editTask.projectId);
      setEditTask(null);
    } catch (error) {
      console.error('Error updating task', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    console.log('Deleting task with ID:', taskId);
    try {
      await deleteTask(editTask.projectId, taskId);
      console.log('Task deleted successfully');
      loadTasks(editTask.projectId);
    } catch (error) {
      console.error('Error deleting task', error);
    }
  };

  const TaskDetails = ({ task }) => (
    <Box>
      <TextField label="Project ID" value={task.projectId} InputProps={{ readOnly: true }} fullWidth />
      <TextField label="Task Title" value={task.title} InputProps={{ readOnly: true }} fullWidth />
      <TextField label="Task Description" value={task.description} InputProps={{ readOnly: true }} fullWidth />
      <TextField label="Task Status" value={task.status} InputProps={{ readOnly: true }} fullWidth />
      <TextField
        label="Due Date"
        type="date"
        value={task.dueDate}
        InputLabelProps={{
          shrink: true,
        }}
        InputProps={{ readOnly: true }}
        fullWidth
      />
      <TextField label="Assigned To" value={task.assignedTo} InputProps={{ readOnly: true }} fullWidth />
    </Box>
  );

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Task Management</Typography>
      <div>
        <TextField
          label="Project ID"
          value={newTask.projectId}
          onChange={(e) => setNewTask({ ...newTask, projectId: e.target.value })}
          fullWidth
        />
        <TextField
          label="Task Title"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
          fullWidth
        />
        <TextField
          label="Task Description"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
          fullWidth
        />
        <TextField
          label="Task Status"
          value={newTask.status}
          onChange={(e) => setNewTask({ ...newTask, status: e.target.value })}
          fullWidth
        />
        <TextField
          label="Due Date"
          type="date"
          value={newTask.dueDate}
          onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
          InputLabelProps={{
            shrink: true,
          }}
          fullWidth
        />
        <TextField
          label="Assigned To"
          value={newTask.assignedTo}
          onChange={(e) => setNewTask({ ...newTask, assignedTo: e.target.value })}
          fullWidth
        />
        <Button variant="contained" color="primary" onClick={handleCreateTask} fullWidth>
          Create Task
        </Button>
      </div>
      {editTask && (
        <div>
          <TextField
            label="Edit Project ID"
            value={editTask.projectId}
            onChange={(e) => setEditTask({ ...editTask, projectId: e.target.value })}
            fullWidth
          />
          <TextField
            label="Edit Task Title"
            value={editTask.title}
            onChange={(e) => setEditTask({ ...editTask, title: e.target.value })}
            fullWidth
          />
          <TextField
            label="Edit Task Description"
            value={editTask.description}
            onChange={(e) => setEditTask({ ...editTask, description: e.target.value })}
            fullWidth
          />
          <TextField
            label="Edit Task Status"
            value={editTask.status}
            onChange={(e) => setEditTask({ ...editTask, status: e.target.value })}
            fullWidth
          />
          <TextField
            label="Edit Due Date"
            type="date"
            value={editTask.dueDate}
            onChange={(e) => setEditTask({ ...editTask, dueDate: e.target.value })}
            InputLabelProps={{
              shrink: true,
            }}
            fullWidth
          />
          <TextField
            label="Edit Assigned To"
            value={editTask.assignedTo}
            onChange={(e) => setEditTask({ ...editTask, assignedTo: e.target.value })}
            fullWidth
          />
          <Button variant="contained" color="primary" onClick={handleUpdateTask} fullWidth>
            Update Task
          </Button>
        </div>
      )}
      <List>
        {tasks.map((task) => (
          <ListItem key={task.id}>
            <TaskDetails task={task} />
            <IconButton edge="end" aria-label="edit" onClick={() => setEditTask(task)}>
              <EditIcon />
            </IconButton>
            <IconButton edge="end" aria-label="delete" onClick={() => handleDeleteTask(task.id)}>
              <DeleteIcon />
            </IconButton>
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default TaskManagement;
