// export default App;

const App = () => {
  const token = localStorage.getItem('token');
  let decodedToken = null;
  let userId = null;

  if (token) {
    decodedToken = jwtDecode(token);
    userId = decodedToken.id;
  }

  const [username, setUsername] = useState('');

  useEffect(() => {
    if (userId) {
      axios.get(`http://localhost:5000/api/users/${userId}`)
        .then(response => {
          setUsername(response.data.username);
        })
        .catch(error => {
          console.error('Error fetching user data', error);
        });
    }
  }, [userId]);

  return (
    <Router>
      <Routes>
        <Route path="/" element={token ? <Dashboard username={username} /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/dashboard" element={token ? <Dashboard username={username} /> : <Navigate to="/login" />} />
        {/* Add more routes for other protected areas */}
      </Routes>
    </Router>
  );
};

export default App;
