import React, { useEffect, useState } from 'react';
import { Typography, Button, List, ListItem, ListItemText, TextField } from '@mui/material';
import { getUsers, createUser } from '../api';

const Users = () => {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({ username: '', password: '', role: 'regular_user' });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await getUsers();
    setUsers(response.data);
  };

  const handleCreateUser = async () => {
    await createUser(newUser);
    fetchUsers();
    setNewUser({ username: '', password: '', role: 'regular_user' });
  };

  return (
    <div>
      <Typography variant="h4">Users</Typography>
      <List>
        {users.map(user => (
          <ListItem key={user.id}>
            <ListItemText primary={user.username} secondary={user.role} />
          </ListItem>
        ))}
      </List>
      <TextField
        label="Username"
        value={newUser.username}
        onChange={(e) => setNewUser({ ...newUser, username: e.target.value })}
      />
      <TextField
        label="Password"
        value={newUser.password}
        onChange={(e) => setNewUser({ ...newUser, password: e.target.value })}
      />
      <TextField
        label="Role"
        value={newUser.role}
        onChange={(e) => setNewUser({ ...newUser, role: e.target.value })}
      />
      <Button variant="contained" color="primary" onClick={handleCreateUser}>
        Create User
      </Button>
    </div>
  );
};

export default Users;
