import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Homepage from './components/Homepage';
import Dashboard from './components/Dashboard';
import UserManagement from './components/Admin/UserManagement';
import ProjectManagement from './components/Admin/ProjectManagement';

const parseJwt = (token) => {
  try {
    const base64Url = token.split('.')[1];
    const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
    const jsonPayload = decodeURIComponent(
      atob(base64)
        .split('')
        .map((c) => {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        })
        .join('')
    );
    return JSON.parse(jsonPayload);
  } catch (e) {
    return null;
  }
};

const App = () => {
  const user = localStorage.getItem('username');
  // const token = localStorage.getItem('token')
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Homepage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/admin/users" element={user?.role === 'admin' ? <UserManagement /> : <Navigate to="/login" />} />
        <Route path="/admin/projects" element={user?.role === 'admin' ? <ProjectManagement /> : <Navigate to="/login" />} />
        <Route path="/dashboard" element={<Dashboard username={user?.username} role={user?.role} />} />
        {/* Add more routes for project management, task management, and file management */}
      </Routes>
    </Router>
  );
};

export default App;
