import axios from 'axios';

const API_URL = 'http://localhost:5000/api';

const token = localStorage.getItem('token');

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Bearer ${token}`,
  },
});

export const login = (credentials) => api.post('/users/login', credentials);

export const register = (user) => api.post('/users/register', user);

// Projects
export const getProjects = () => api.get('/projects');
export const createProject = (project) => api.post('/projects', project);
export const updateProject = (projectId, project) => api.put(`/projects/${projectId}`, project);
export const deleteProject = (projectId) => api.delete(`/projects/${projectId}`);

// Tasks
export const getTasks = (projectId) => api.get(`/projects/${projectId}/tasks`);
export const createTask = (projectId, task) => api.post(`/projects/${projectId}/tasks`, task);
export const updateTask = (projectId, taskId, task) => api.put(`/projects/${projectId}/tasks/${taskId}`, task);
export const deleteTask = (projectId, taskId) => api.delete(`/projects/${projectId}/tasks/${taskId}`);

// Users
export const getUsers = () => api.get('/admin');
export const createUser = (user) => api.post('/admin', user);
export const updateUser = (userId, role) => api.put(`/admin/${userId}/role`, { role });
export const deleteUser = (userId) => api.delete(`/admin/${userId}`);
export const assignAdmin = (userId) => api.put(`/admin/${userId}/assignAdmin`);
export const revokeAdmin = (userId) => api.put(`/admin/${userId}/revokeAdmin`);

// Project Team
export const addTeamMember = (projectId, userId) => api.post(`/projects/${projectId}/team/${userId}`);
export const removeTeamMember = (projectId, userId) => api.delete(`/projects/${projectId}/team/${userId}`);
