import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Auth/Login';
import Register from './components/Auth/Register';
import Homepage from './components/Common/Homepage';
import Dashboard from './components/Common/Dashboard';
import UserManagement from './components/Admin/UserManagement';
import ProjectManagement from './components/Admin/ProjectManagement';


const App = () => {
  //const user = localStorage.getItem('username');
  let token = localStorage.getItem('token');
  //let user = null;
  // if (token) {
  //   user = parseJwt(token);
  //   console.log("Parsed Token:", user); // Log the parsed token
  // }
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Homepage />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        <Route path="/admin/users" element={user?.role === 'admin' ? <UserManagement /> : <Navigate to="/login" />} />
        <Route path="/admin/projects" element={user?.role === 'admin' ? <ProjectManagement /> : <Navigate to="/login" />} />
        <Route path="/dashboard" element={<Dashboard username={user?.username} role={user?.role} />} />
        {/* Add more routes for project management, task management, and file management */}
      </Routes>
    </Router>
  );
};

export default App;
